    def calntxt(self,sal,mah):
        label = []
        a=0
        c=1
        b=1
        for d in range(42):
                                               
            if d < self.firstweekofmonth(sal,mah):
                
                                 
                label.append((khayyam.JalaliDate(sal,mah,1)-
                               timedelta(days=
                             (self.firstweekofmonth(sal,mah)-d))).strftime('%d'))
                                             
                                
                                
            if d >= self.firstweekofmonth(sal,mah):
                if c > khayyam.JalaliDate(sal,mah).daysinmonth:
                    a=a+1
                    label.append(str(a))
                else:
                    label.append(str(c))
                    c=c+1
                        
                                
        return label
